@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
    font-family: var(--font-sans);
  }

  h1, h2, h3, h4, h5, h6 {
    font-family: var(--font-sans);
    font-weight: 600;
  }
}

@layer components {
  .btn-primary {
    @apply bg-primary text-primary-foreground hover:bg-primary/90 focus:ring-2 focus:ring-primary focus:ring-offset-2;
  }

  .btn-secondary {
    @apply bg-secondary text-secondary-foreground hover:bg-secondary/90;
  }

  .btn-accent {
    @apply bg-accent text-accent-foreground hover:bg-accent/90;
  }

  .gradient-primary {
    background: linear-gradient(135deg, hsl(222 84% 35%) 0%, hsl(222 84% 25%) 100%);
  }

  .text-gradient {
    background: linear-gradient(135deg, hsl(222 84% 35%) 0%, hsl(158 64% 52%) 100%);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
  }

  .shadow-custom {
    box-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.1), 0 2px 4px -1px rgba(0, 0, 0, 0.06);
  }

  .shadow-custom-lg {
    box-shadow: 0 10px 15px -3px rgba(0, 0, 0, 0.1), 0 4px 6px -2px rgba(0, 0, 0, 0.05);
  }

  .border-custom {
    border-color: hsl(222 84% 35% / 0.2);
  }

  .security-card {
    @apply bg-white border border-gray-200 rounded-xl shadow-sm hover:shadow-md transition-shadow duration-200;
  }

  .officer-interface {
    @apply bg-gray-50 min-h-screen;
  }

  .progress-bar {
    @apply bg-gray-200 rounded-full h-2 overflow-hidden;
  }

  .progress-bar-fill {
    @apply bg-primary h-full transition-all duration-300 ease-in-out;
  }
}

@layer utilities {
  .text-balance {
    text-wrap: balance;
  }

  .font-security {
    font-family: var(--font-sans);
    font-weight: 600;
  }

  .animate-fade-in {
    animation: fadeIn 0.5s ease-in-out;
  }

  .animate-slide-up {
    animation: slideUp 0.3s ease-out;
  }

  .bg-security-pattern {
    background-image: 
      radial-gradient(circle at 25% 25%, hsl(222 84% 35% / 0.1) 0%, transparent 50%),
      radial-gradient(circle at 75% 75%, hsl(158 64% 52% / 0.1) 0%, transparent 50%);
  }
}

@keyframes fadeIn {
  from { opacity: 0; }
  to { opacity: 1; }
}

@keyframes slideUp {
  from { 
    opacity: 0;
    transform: translateY(20px);
  }
  to { 
    opacity: 1;
    transform: translateY(0);
  }
}

/* Form focus states */
input:focus,
select:focus,
textarea:focus {
  outline: none;
  box-shadow: 0 0 0 3px hsl(222 84% 35% / 0.1);
}

/* Button hover effects */
button:hover {
  transform: translateY(-1px);
}

/* Card hover effects */
.hover\:shadow-md:hover {
  box-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.1), 0 2px 4px -1px rgba(0, 0, 0, 0.06);
}

/* Security specific styling */
.security-gradient {
  background: linear-gradient(135deg, hsl(222 84% 35%) 0%, hsl(222 84% 45%) 100%);
}

.accent-gradient {
  background: linear-gradient(135deg, hsl(158 64% 52%) 0%, hsl(158 64% 42%) 100%);
}

/* Mobile responsiveness improvements */
@media (max-width: 768px) {
  .mobile-padding {
    padding-left: 1rem;
    padding-right: 1rem;
  }

  .mobile-text-sm {
    font-size: 0.875rem;
  }
}

/* Print styles for reports */
@media print {
  .no-print {
    display: none !important;
  }

  .print-break {
    page-break-before: always;
  }
}

