import { useState } from "react";
import { useParams } from "wouter";
import { useAuth } from "@/hooks/use-auth";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Progress } from "@/components/ui/progress";
import { 
  ArrowLeft, 
  Camera, 
  Upload, 
  Plus, 
  Save, 
  CheckCircle, 
  Edit,
  Tv,
  Gem,
  Laptop
} from "lucide-react";
import { Link, useLocation } from "wouter";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";

interface AuditItem {
  id?: string;
  category: string;
  description: string;
  estimatedValue: string;
  serialNumber: string;
}

export default function OfficerAudit() {
  const { id } = useParams();
  const { user } = useAuth();
  const [, setLocation] = useLocation();
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Redirect non-officer users
  if (user && user.role !== 'officer') {
    setLocation('/');
    return null;
  }

  const [currentItem, setCurrentItem] = useState<AuditItem>({
    category: '',
    description: '',
    estimatedValue: '',
    serialNumber: '',
  });

  const { data: appointment, isLoading: appointmentLoading } = useQuery<any>({
    queryKey: ["/api/appointments", id],
    enabled: !!id,
  });

  const { data: auditItems, isLoading: itemsLoading } = useQuery<any[]>({
    queryKey: ["/api/audit-items", id],
    enabled: !!id,
  });

  const addItemMutation = useMutation({
    mutationFn: async (item: any) => {
      const res = await apiRequest("POST", "/api/audit-items", {
        ...item,
        appointmentId: id,
      });
      return await res.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/audit-items", id] });
      setCurrentItem({
        category: '',
        description: '',
        estimatedValue: '',
        serialNumber: '',
      });
      toast({
        title: "Item added successfully",
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Failed to add item",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const generateReportMutation = useMutation({
    mutationFn: async () => {
      const res = await apiRequest("POST", `/api/reports/generate/${id}`);
      return await res.json();
    },
    onSuccess: () => {
      toast({
        title: "Report generated successfully",
        description: "The audit report has been created",
      });
      setLocation("/officer");
    },
    onError: (error: Error) => {
      toast({
        title: "Failed to generate report",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const handleInputChange = (field: keyof AuditItem, value: string) => {
    setCurrentItem(prev => ({ ...prev, [field]: value }));
  };

  const handleAddItem = () => {
    if (!currentItem.category || !currentItem.description) {
      toast({
        title: "Please fill in required fields",
        description: "Category and description are required",
        variant: "destructive",
      });
      return;
    }

    addItemMutation.mutate({
      ...currentItem,
      estimatedValue: parseFloat(currentItem.estimatedValue) || 0,
    });
  };

  const totalItems = auditItems?.length || 0;
  const progressPercentage = Math.min((totalItems / 15) * 100, 100);

  const categories = [
    "Electronics",
    "Jewelry", 
    "Furniture",
    "Artwork",
    "Appliances",
    "Other"
  ];

  const getCategoryIcon = (category: string) => {
    switch (category.toLowerCase()) {
      case 'electronics':
        return <Tv className="text-gray-500" size={20} />;
      case 'jewelry':
        return <Gem className="text-gray-500" size={20} />;
      default:
        return <Laptop className="text-gray-500" size={20} />;
    }
  };

  if (appointmentLoading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary mx-auto mb-4"></div>
          <p>Loading audit details...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Audit Header */}
      <div className="bg-primary text-white p-4">
        <div className="flex items-center justify-between">
          <Link href="/officer">
            <Button variant="ghost" className="text-blue-100 hover:text-white p-0" data-testid="button-back">
              <ArrowLeft size={20} />
            </Button>
          </Link>
          <div className="text-center">
            <div className="font-semibold" data-testid="text-audit-title">Home Audit</div>
            <div className="text-sm text-blue-100" data-testid="text-customer-location">
              {appointment?.fullName} - {appointment?.address}
            </div>
          </div>
          <div className="text-right">
            <div className="text-sm">{appointment?.preferredTime}</div>
            <div className="text-xs text-blue-100">Started</div>
          </div>
        </div>
      </div>

      <div className="p-4">
        {/* Audit Progress */}
        <Card className="mb-6" data-testid="card-audit-progress">
          <CardContent className="p-4">
            <div className="flex justify-between items-center mb-3">
              <h3 className="font-semibold text-gray-900">Audit Progress</h3>
              <span className="text-sm text-gray-500" data-testid="text-progress-count">
                {totalItems} of 15 items
              </span>
            </div>
            <Progress value={progressPercentage} className="w-full" data-testid="progress-audit" />
          </CardContent>
        </Card>

        {/* Current Item Documentation */}
        <Card className="mb-6" data-testid="card-document-item">
          <CardHeader>
            <CardTitle>Document Item</CardTitle>
          </CardHeader>
          <CardContent className="space-y-4">
            {/* Item Category */}
            <div>
              <Label className="text-sm font-medium text-gray-700 mb-2">Category</Label>
              <Select 
                value={currentItem.category} 
                onValueChange={(value) => handleInputChange("category", value)}
              >
                <SelectTrigger data-testid="select-category">
                  <SelectValue placeholder="Select category" />
                </SelectTrigger>
                <SelectContent>
                  {categories.map((category) => (
                    <SelectItem key={category} value={category}>
                      {category}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>

            {/* Item Description */}
            <div>
              <Label className="text-sm font-medium text-gray-700 mb-2">Description</Label>
              <Input
                type="text"
                value={currentItem.description}
                onChange={(e) => handleInputChange("description", e.target.value)}
                placeholder="e.g., Samsung 65-inch Smart TV"
                data-testid="input-description"
              />
            </div>

            {/* Value and Serial */}
            <div className="grid grid-cols-2 gap-4">
              <div>
                <Label className="text-sm font-medium text-gray-700 mb-2">Estimated Value</Label>
                <Input
                  type="number"
                  value={currentItem.estimatedValue}
                  onChange={(e) => handleInputChange("estimatedValue", e.target.value)}
                  placeholder="2000"
                  data-testid="input-estimated-value"
                />
              </div>
              <div>
                <Label className="text-sm font-medium text-gray-700 mb-2">Serial/Model</Label>
                <Input
                  type="text"
                  value={currentItem.serialNumber}
                  onChange={(e) => handleInputChange("serialNumber", e.target.value)}
                  placeholder="Model #"
                  data-testid="input-serial-number"
                />
              </div>
            </div>

            {/* Photo Upload */}
            <div>
              <Label className="text-sm font-medium text-gray-700 mb-2">Item Photo</Label>
              <Card className="border-2 border-dashed border-gray-300 p-6 text-center" data-testid="photo-upload-area">
                <Camera className="text-4xl text-gray-400 mb-3 mx-auto" />
                <div className="text-gray-600 mb-2">Take Photo</div>
                <Button className="bg-primary text-white" data-testid="button-open-camera">
                  <Camera className="mr-2" size={16} />
                  Open Camera
                </Button>
              </Card>
            </div>

            {/* Receipt Upload */}
            <div>
              <Label className="text-sm font-medium text-gray-700 mb-2">Receipt (Optional)</Label>
              <Card className="border-2 border-dashed border-gray-300 p-4 text-center" data-testid="receipt-upload-area">
                <Upload className="text-2xl text-gray-400 mb-2 mx-auto" />
                <div className="text-sm text-gray-600 mb-2">Upload Receipt</div>
                <Button variant="secondary" size="sm" data-testid="button-choose-file">
                  <Upload className="mr-2" size={14} />
                  Choose File
                </Button>
              </Card>
            </div>

            {/* Action Buttons */}
            <div className="flex space-x-3 pt-4">
              <Button 
                onClick={handleAddItem}
                disabled={addItemMutation.isPending}
                className="flex-1 bg-accent text-white hover:bg-green-600"
                data-testid="button-add-item"
              >
                <Plus className="mr-2" size={16} />
                {addItemMutation.isPending ? "Adding..." : "Add Item"}
              </Button>
              <Button 
                variant="secondary" 
                className="flex-1"
                data-testid="button-save-draft"
              >
                <Save className="mr-2" size={16} />
                Save Draft
              </Button>
            </div>
          </CardContent>
        </Card>

        {/* Documented Items */}
        <Card className="mb-6" data-testid="card-documented-items">
          <CardHeader>
            <CardTitle>Documented Items ({totalItems})</CardTitle>
          </CardHeader>
          <CardContent>
            {itemsLoading ? (
              <div className="text-center py-4">Loading items...</div>
            ) : !auditItems?.length ? (
              <div className="text-center py-8 text-gray-500" data-testid="no-items-message">
                No items documented yet
              </div>
            ) : (
              <div className="divide-y divide-gray-200">
                {auditItems?.map((item: any) => (
                  <div key={item.id} className="py-4 flex items-center justify-between" data-testid={`item-${item.id}`}>
                    <div className="flex items-center">
                      <div className="w-10 h-10 bg-gray-200 rounded mr-3 flex items-center justify-center">
                        {getCategoryIcon(item.category)}
                      </div>
                      <div>
                        <div className="font-medium text-gray-900" data-testid={`item-description-${item.id}`}>
                          {item.description}
                        </div>
                        <div className="text-sm text-gray-500" data-testid={`item-details-${item.id}`}>
                          {item.category} - ${item.estimatedValue?.toLocaleString()}
                        </div>
                      </div>
                    </div>
                    <Button variant="ghost" size="sm" data-testid={`button-edit-item-${item.id}`}>
                      <Edit size={16} />
                    </Button>
                  </div>
                ))}
              </div>
            )}
          </CardContent>
        </Card>

        {/* Complete Audit */}
        <Card data-testid="card-complete-audit">
          <CardContent className="p-4">
            <Button 
              onClick={() => generateReportMutation.mutate()}
              disabled={generateReportMutation.isPending || totalItems === 0}
              className="w-full bg-accent text-white hover:bg-green-600 py-3 text-lg font-semibold"
              data-testid="button-complete-audit"
            >
              <CheckCircle className="mr-2" size={20} />
              {generateReportMutation.isPending 
                ? "Generating Report..." 
                : "Complete Audit & Generate Report"
              }
            </Button>
            <p className="text-xs text-gray-500 text-center mt-2">
              This will generate the PDF report and request homeowner signature
            </p>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

client/src/pages/officer-dashboard.tsx.content:
import { useAuth } from "@/hooks/use-auth";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { 
  Shield, 
  Calendar, 
  FileText, 
  User,
  LogOut,
  Clock,
  MapPin,
  Play
} from "lucide-react";
import { Link, useLocation } from "wouter";
import { useQuery } from "@tanstack/react-query";

export default function OfficerDashboard() {
  const { user, logoutMutation } = useAuth();
  const [, setLocation] = useLocation();

  // Redirect non-officer users
  if (user && user.role !== 'officer') {
    setLocation('/');
    return null;
  }

  const { data: appointments, isLoading } = useQuery<any[]>({
    queryKey: ["/api/appointments"],
  });

  const handleLogout = () => {
    logoutMutation.mutate(undefined, {
      onSuccess: () => setLocation("/"),
    });
  };

  const todayAppointments = appointments?.filter((apt: any) => {
    const today = new Date().toISOString().split('T')[0];
    return apt.preferredDate === today;
  }) || [];

  const getStatusBadge = (status: string) => {
    const variants: Record<string, "default" | "secondary" | "destructive" | "outline"> = {
      scheduled: "outline",
      in_progress: "default", 
      completed: "secondary",
      cancelled: "destructive",
    };

    return (
      <Badge variant={variants[status] || "outline"} data-testid={`badge-status-${status}`}>
        {status.charAt(0).toUpperCase() + status.slice(1).replace('_', ' ')}
      </Badge>
    );
  };

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Mobile Header */}
      <div className="bg-primary text-white p-4">
        <div className="flex items-center justify-between">
          <div className="flex items-center">
            <Shield className="text-xl mr-3" />
            <div>
              <div className="font-semibold" data-testid="text-officer-dashboard">Officer Dashboard</div>
              <div className="text-sm text-blue-100" data-testid="text-officer-name">
                {user?.fullName || user?.username}
              </div>
            </div>
          </div>
          <Button 
            variant="ghost" 
            onClick={handleLogout}
            className="text-blue-100 hover:text-white"
            data-testid="button-logout"
          >
            <LogOut size={16} />
          </Button>
        </div>
      </div>

      <div className="p-4">
        {/* Today's Appointments */}
        <Card className="mb-6" data-testid="card-todays-appointments">
          <CardHeader>
            <CardTitle className="flex items-center">
              <Calendar className="mr-2" size={20} />
              Today's Appointments
            </CardTitle>
          </CardHeader>
          <CardContent>
            {isLoading ? (
              <div className="text-center py-4">Loading appointments...</div>
            ) : todayAppointments.length === 0 ? (
              <div className="text-center py-8 text-gray-500" data-testid="no-appointments-today">
                No appointments scheduled for today
              </div>
            ) : (
              <div className="space-y-4">
                {todayAppointments.map((appointment: any) => (
                  <Card key={appointment.id} className="border" data-testid={`card-appointment-${appointment.id}`}>
                    <CardContent className="p-4">
                      <div className="flex justify-between items-start mb-3">
                        <div>
                          <div className="font-medium text-gray-900" data-testid={`text-customer-name-${appointment.id}`}>
                            {appointment.fullName}
                          </div>
                          <div className="text-sm text-gray-500 flex items-center" data-testid={`text-address-${appointment.id}`}>
                            <MapPin size={14} className="mr-1" />
                            {appointment.address}
                          </div>
                        </div>
                        <div className="text-right">
                          <div className="text-sm font-medium text-primary flex items-center" data-testid={`text-time-${appointment.id}`}>
                            <Clock size={14} className="mr-1" />
                            {appointment.preferredTime}
                          </div>
                          <div className="text-xs text-gray-500">{appointment.preferredDate}</div>
                        </div>
                      </div>
                      <div className="flex justify-between items-center">
                        {getStatusBadge(appointment.status)}
                        <Link href={`/officer/audit/${appointment.id}`}>
                          <Button 
                            size="sm" 
                            className="bg-primary text-white"
                            data-testid={`button-start-audit-${appointment.id}`}
                          >
                            <Play size={14} className="mr-1" />
                            Start Audit
                          </Button>
                        </Link>
                      </div>
                    </CardContent>
                  </Card>
                ))}
              </div>
            )}
          </CardContent>
        </Card>

        {/* Quick Stats */}
        <div className="grid grid-cols-2 gap-4 mb-6">
          <Card data-testid="stat-audits-today">
            <CardContent className="p-4 text-center">
              <div className="text-2xl font-bold text-primary">{todayAppointments.length}</div>
              <div className="text-sm text-gray-600">Audits Today</div>
            </CardContent>
          </Card>
          <Card data-testid="stat-total-completed">
            <CardContent className="p-4 text-center">
              <div className="text-2xl font-bold text-accent">
                {appointments?.filter((apt: any) => apt.status === 'completed').length || 0}
              </div>
              <div className="text-sm text-gray-600">Total Completed</div>
            </CardContent>
          </Card>
        </div>

        {/* Quick Actions */}
        <Card data-testid="card-quick-actions">
          <CardHeader>
            <CardTitle>Quick Actions</CardTitle>
          </CardHeader>
          <CardContent className="space-y-3">
            <Button 
              className="w-full bg-primary text-white justify-start"
              data-testid="button-view-schedule"
            >
              <Calendar className="mr-3" size={16} />
              View Schedule
            </Button>
            <Button 
              variant="outline" 
              className="w-full justify-start"
              data-testid="button-recent-reports"
            >
              <FileText className="mr-3" size={16} />
              Recent Reports
            </Button>
            <Button 
              variant="outline" 
              className="w-full justify-start"
              data-testid="button-profile-settings"
            >
              <User className="mr-3" size={16} />
              Profile Settings
            </Button>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

client/src/pages/signup-page.tsx.content:
import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Checkbox } from "@/components/ui/checkbox";
import { ArrowLeft, Calendar, Lock, CreditCard } from "lucide-react";
import { Link, useLocation } from "wouter";
import { useToast } from "@/hooks/use-toast";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { DatePicker } from "@/components/ui/date-picker";
import { SquarePaymentForm } from "@/components/square-payment-form";

interface SignupFormData {
  fullName: string;
  email: string;
  phone: string;
  address: string;
  preferredDate: string;
  preferredTime: string;
  hasReceiptsReady: boolean;
  addTitleProtection: boolean;
}

interface PaymentData {
  sourceId: string;
  amount: number;
  appointmentId: string;
  idempotencyKey: string;
}

export default function SignupPage() {
  const [, setLocation] = useLocation();
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [formData, setFormData] = useState<SignupFormData>({
    fullName: "",
    email: "",
    phone: "",
    address: "",
    preferredDate: "",
    preferredTime: "",
    hasReceiptsReady: false,
    addTitleProtection: false,
  });
  const [showPayment, setShowPayment] = useState(false);
  const [appointmentId, setAppointmentId] = useState<string | null>(null);
  const [isProcessingPayment, setIsProcessingPayment] = useState(false);

  const createAppointmentMutation = useMutation({
    mutationFn: async (data: any) => {
      const res = await apiRequest("POST", "/api/appointments", data);
      return await res.json();
    },
    onSuccess: (appointment) => {
      queryClient.invalidateQueries({ queryKey: ["/api/appointments"] });
      setAppointmentId(appointment.id);

      if (formData.addTitleProtection) {
        setShowPayment(true);
        toast({
          title: "Appointment created!",
          description: "Please complete payment for Title Protection.",
        });
      } else {
        toast({
          title: "Appointment booked successfully!",
          description: "You will receive a confirmation email shortly.",
        });
        setLocation(`/confirmation/${appointment.id}`);
      }
    },
    onError: (error: Error) => {
      toast({
        title: "Booking failed",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const createPaymentMutation = useMutation({
    mutationFn: async (data: PaymentData) => {
      const res = await apiRequest("POST", "/api/payments", data);
      return await res.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/payments"] });
      toast({
        title: "Payment successful!",
        description: "Your Title Protection subscription is now active.",
      });
      setLocation(`/confirmation/${appointmentId}`);
    },
    onError: (error: Error) => {
      toast({
        title: "Payment failed",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    // Basic validation
    if (!formData.fullName || !formData.email || !formData.phone || !formData.address || !formData.preferredDate || !formData.preferredTime) {
      toast({
        title: "Please fill in all required fields",
        variant: "destructive",
      });
      return;
    }

    if (!formData.hasReceiptsReady) {
      toast({
        title: "Please confirm you will have receipts ready",
        variant: "destructive",
      });
      return;
    }

    // Check if date is within 7 days
    const selectedDate = new Date(formData.preferredDate);
    const today = new Date();
    const maxDate = new Date(today.getTime() + 7 * 24 * 60 * 60 * 1000);

    if (selectedDate < today || selectedDate > maxDate) {
      toast({
        title: "Invalid date",
        description: "Please select a date within the next 7 days",
        variant: "destructive",
      });
      return;
    }

    const appointmentData = {
      fullName: formData.fullName,
      email: formData.email,
      phone: formData.phone,
      address: formData.address,
      preferredDate: formData.preferredDate,
      preferredTime: formData.preferredTime,
      hasReceiptsReady: formData.hasReceiptsReady,
      status: "scheduled",
    };

    if (!formData.addTitleProtection) {
      // Free service only - create appointment directly
      createAppointmentMutation.mutate(appointmentData);
    } else {
      // Has paid service - create appointment first, then show payment
      createAppointmentMutation.mutate(appointmentData);
    }
  };

  const handleInputChange = (field: keyof SignupFormData, value: string | boolean) => {
    setFormData(prev => ({ ...prev, [field]: value }));
  };

  const handlePaymentSuccess = async (sourceId: string, paymentResult: any) => {
    if (!appointmentId) {
      toast({
        title: "Error",
        description: "No appointment found for payment",
        variant: "destructive",
      });
      return;
    }

    setIsProcessingPayment(true);

    const paymentData: PaymentData = {
      sourceId,
      amount: 50, // $50 for title protection
      appointmentId,
      idempotencyKey: `payment-${appointmentId}-${Date.now()}`
    };

    createPaymentMutation.mutate(paymentData);
    setIsProcessingPayment(false);
  };

  const handlePaymentError = (error: string) => {
    toast({
      title: "Payment Error",
      description: error,
      variant: "destructive",
    });
    setIsProcessingPayment(false);
  };

  // Calculate available dates (next 7 days)
  const getAvailableDates = () => {
    const dates = [];
    for (let i = 1; i <= 7; i++) {
      const date = new Date();
      date.setDate(date.getDate() + i);
      dates.push(date.toISOString().split('T')[0]);
    }
    return dates;
  };

  const availableTimes = [
    "9:00 AM",
    "11:00 AM", 
    "1:00 PM",
    "3:00 PM",
    "5:00 PM"
  ];

  return (
    <div className="min-h-screen bg-gray-50 py-12">
      <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* Progress Indicator */}
        <div className="mb-8">
          <div className="flex items-center justify-center">
            <div className="flex items-center">
              <div className="flex items-center justify-center w-8 h-8 bg-primary text-white rounded-full text-sm font-medium">1</div>
              <span className="ml-2 text-sm font-medium text-primary">Booking Details</span>
            </div>
            <div className="mx-4 h-px bg-gray-300 w-16"></div>
            <div className="flex items-center">
              <div className="flex items-center justify-center w-8 h-8 bg-gray-300 text-gray-600 rounded-full text-sm font-medium">2</div>
              <span className="ml-2 text-sm font-medium text-gray-500">Confirmation</span>
            </div>
          </div>
        </div>

        <Card className="shadow-lg" data-testid="card-signup-form">
          <CardHeader className="text-center">
            <CardTitle className="text-3xl font-bold text-gray-900 mb-2">
              Schedule Your Free Home Audit
            </CardTitle>
            <p className="text-gray-600">
              Fill out the form below to book your appointment with a licensed security officer
            </p>
          </CardHeader>

          <CardContent className="p-8">
            <form onSubmit={handleSubmit} className="space-y-6">
              {/* Personal Information */}
              <div className="grid md:grid-cols-2 gap-6">
                <div>
                  <Label htmlFor="fullName" className="text-sm font-medium text-gray-700 mb-2">
                    Full Name *
                  </Label>
                  <Input
                    id="fullName"
                    type="text"
                    value={formData.fullName}
                    onChange={(e) => handleInputChange("fullName", e.target.value)}
                    placeholder="John Doe"
                    required
                    data-testid="input-full-name"
                  />
                </div>
                <div>
                  <Label htmlFor="email" className="text-sm font-medium text-gray-700 mb-2">
                    Email Address *
                  </Label>
                  <Input
                    id="email"
                    type="email"
                    value={formData.email}
                    onChange={(e) => handleInputChange("email", e.target.value)}
                    placeholder="john@example.com"
                    required
                    data-testid="input-email"
                  />
                </div>
              </div>

              <div>
                <Label htmlFor="phone" className="text-sm font-medium text-gray-700 mb-2">
                  Mobile Phone Number *
                </Label>
                <Input
                  id="phone"
                  type="tel"
                  value={formData.phone}
                  onChange={(e) => handleInputChange("phone", e.target.value)}
                  placeholder="(555) 123-4567"
                  required
                  data-testid="input-phone"
                />
              </div>

              {/* Address */}
              <div>
                <Label htmlFor="address" className="text-sm font-medium text-gray-700 mb-2">
                  Home Address (Service Location) *
                </Label>
                <Input
                  id="address"
                  type="text"
                  value={formData.address}
                  onChange={(e) => handleInputChange("address", e.target.value)}
                  placeholder="123 Main Street, City, State, ZIP"
                  required
                  data-testid="input-address"
                />
              </div>

              {/* Date & Time Selection */}
              <div className="grid md:grid-cols-2 gap-6">
                <div>
                  <Label className="text-sm font-medium text-gray-700 mb-2">
                    Preferred Date *
                  </Label>
                  <Input
                    type="date"
                    value={formData.preferredDate}
                    onChange={(e) => handleInputChange("preferredDate", e.target.value)}
                    min={new Date(Date.now() + 24 * 60 * 60 * 1000).toISOString().split('T')[0]}
                    max={new Date(Date.now() + 7 * 24 * 60 * 60 * 1000).toISOString().split('T')[0]}
                    required
                    data-testid="input-preferred-date"
                  />
                  <p className="text-xs text-gray-500 mt-1">Available within the next 7 days</p>
                </div>
                <div>
                  <Label className="text-sm font-medium text-gray-700 mb-2">
                    Preferred Time *
                  </Label>
                  <Select 
                    value={formData.preferredTime} 
                    onValueChange={(value) => handleInputChange("preferredTime", value)}
                  >
                    <SelectTrigger data-testid="select-preferred-time">
                      <SelectValue placeholder="Select a time" />
                    </SelectTrigger>
                    <SelectContent>
                      {availableTimes.map((time) => (
                        <SelectItem key={time} value={time}>
                          {time}
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                </div>
              </div>

              {/* Confirmation Checkbox */}
              <div className="flex items-start space-x-3">
                <Checkbox
                  id="receipts-ready"
                  checked={formData.hasReceiptsReady}
                  onCheckedChange={(checked) => handleInputChange("hasReceiptsReady", !!checked)}
                  data-testid="checkbox-receipts-ready"
                />
                <Label htmlFor="receipts-ready" className="text-sm text-gray-600">
                  I will have my receipts and valuables ready for documentation at the scheduled appointment time.
                </Label>
              </div>

              {/* Payment Section */}
              <div className="border-t pt-8">
                <h3 className="text-xl font-semibold text-gray-900 mb-6">Payment Information</h3>

                {/* Service Options */}
                <div className="space-y-4 mb-6">
                  <Card>
                    <CardContent className="p-4">
                      <div className="flex items-center justify-between">
                        <div>
                          <div className="font-medium text-gray-900">Free Home Audit</div>
                          <div className="text-sm text-gray-600">Professional documentation of your valuables</div>
                        </div>
                        <div className="text-lg font-semibold text-accent">FREE</div>
                      </div>
                    </CardContent>
                  </Card>

                  <Card>
                    <CardContent className="p-4">
                      <div className="flex items-start space-x-3">
                        <Checkbox
                          id="title-protection"
                          checked={formData.addTitleProtection}
                          onCheckedChange={(checked) => handleInputChange("addTitleProtection", !!checked)}
                          data-testid="checkbox-title-protection"
                        />
                        <div className="flex-1">
                          <div className="flex items-center justify-between">
                            <div>
                              <Label htmlFor="title-protection" className="font-medium text-gray-900">
                                Title Protection Monitoring
                              </Label>
                              <div className="text-sm text-gray-600">Monthly monitoring of your property title for fraudulent activity</div>
                            </div>
                            <div className="text-lg font-semibold text-primary">$50/month</div>
                          </div>
                        </div>
                      </div>
                    </CardContent>
                  </Card>
                </div>

                {/* Square Payment Form Integration - Only show if title protection is selected */}
                {formData.addTitleProtection && (
                  <SquarePaymentForm
                    amount={50}
                    onPaymentSuccess={handlePaymentSuccess}
                    onPaymentError={handlePaymentError}
                    disabled={isProcessingPayment}
                  />
                )}
              </div>

              {/* Order Summary */}
              <Card className="bg-gray-50">
                <CardContent className="p-6">
                  <h4 className="font-medium text-gray-900 mb-4">Order Summary</h4>
                  <div className="space-y-2">
                    <div className="flex justify-between">
                      <span className="text-gray-600">Home Audit Service</span>
                      <span className="font-medium">FREE</span>
                    </div>
                    {formData.addTitleProtection && (
                      <div className="flex justify-between text-sm text-gray-500">
                        <span>Title Protection</span>
                        <span>$50.00/month</span>
                      </div>
                    )}
                    <div className="border-t pt-2 mt-2">
                      <div className="flex justify-between font-semibold">
                        <span>Total Today</span>
                        <span className="text-accent">FREE</span>
                      </div>
                    </div>
                  </div>
                </CardContent>
              </Card>

              {/* Submit Button */}
              <div className="text-center pt-6">
                {showPayment ? (
                  <div className="text-center">
                    <p className="text-gray-600 mb-4">
                      Complete your Title Protection payment to finish booking
                    </p>
                    <SquarePaymentForm
                      amount={50}
                      onPaymentSuccess={handlePaymentSuccess}
                      onPaymentError={handlePaymentError}
                      disabled={isProcessingPayment || createPaymentMutation.isPending}
                    />
                  </div>
                ) : (
                  <Button
                    type="submit"
                    className="w-full bg-accent text-white hover:bg-green-600 py-4 text-lg font-semibold"
                    disabled={createAppointmentMutation.isPending}
                    data-testid="button-complete-booking"
                  >
                    <Calendar className="mr-2" />
                    {createAppointmentMutation.isPending 
                      ? "Processing..."
                      : formData.addTitleProtection 
                        ? "Continue to Payment"
                        : "Complete Booking & Schedule Audit"
                    }
                  </Button>
                )}
                <p className="text-xs text-gray-500 mt-2">
                  By clicking submit, you agree to our Terms of Service and Privacy Policy
                </p>
              </div>
            </form>
          </CardContent>
        </Card>

        {/* Back to Landing */}
        <div className="text-center mt-8">
          <Link href="/">
            <Button variant="ghost" data-testid="button-back-home">
              <ArrowLeft className="mr-2" />
              Back to Home
            </Button>
          </Link>
        </div>
      </div>
    </div>
  );
}

